mapper.select:
select no, title, writer, content, regtime
  from vue_board 
 where no = #{no}

select 	no, writer, content, writetime
from	vue_reply
where	bno = #{no}

mapper.insert:
insert into vue_board(
	title, writer, content
) values (
	#{title}, #{writer}, #{content}
)

mapper.update:
update vue_board 
   set title = #{title}, 
   	   writer = #{writer}, 
   	   content = #{content}
 where no = #{no}

update vue_reply
	set content = #{content}
where no = #{no}
