mapper.select:
select 	distinct h.no, h.AptName, h.dealAmount, h.area, h.dealYear, h.dealMonth, h.dealDay, h.floor, h.jibun, b.city, b.gugun, b.dong, i.lat, i.lng, i.buildYear
from	housedeal h 
left 	outer join baseaddress b
using	(dong)
left 	outer join houseinfo i
on		h.AptName = i.AptName and h.dong = i.dong
where	h.dong = #{dong}
order	by h.no
limit	#{start}, #{spp}

select 	*
from	corona c 
left 	outer join baseaddress b
on		c.sigungu = b.gugun
where	b.dong = #{dong}
limit	#{start}, #{spp}

select 	distinct gugun, dongcode 
from	baseaddress 
where	city=#{city}

select	dong
from	baseaddress
where 	dongcode = #{dongcode}

select count(no) from housedeal
AptName like concat('%', #{word}, '%')
dong = #{word}
