mapper.select:
select 	h.no, h.dong, h.AptName, h.dealAmount, h.area, h.dealYear, h.dealMonth, h.dealDay, h.type, b.lat, b.lng
from	housedeal h left outer join baseaddress b
using	(dong)
where	AptName like concat('%', #{aptName}, '%')
order 	by no 
limit	#{start}, #{spp}

select 	h.no, h.dong, h.AptName, h.dealAmount, h.area, h.dealYear, h.dealMonth, h.dealDay, h.type, b.lat, b.lng
from	housedeal h left outer join baseaddress b
using	(dong)
where	dong = #{dong}
order	by no
limit	#{start}, #{spp}

select 	distinct gugun, dongcode 
from	baseaddress 
where	city=#{city}

select	dong
from	baseaddress
where 	dongcode = #{dongcode}

select count(no) from housedeal
AptName like concat('%', #{word}, '%')
dong = #{word}
